import platform
import ctypes


def windows_only(func):
    if platform.system() != 'Windows':
        return lambda *args, **kwargs: None
    return func


@windows_only
def hide_file(path):
    """ Set the hidden attribute on a file or directory.
    Arguments:
    path -- Path to the file whose attributes will be modified. Must be a text string.
    """
    __import__('ctypes.wintypes')

    fnSetFileAttr = ctypes.windll.kernel32.SetFileAttributesW
    fnSetFileAttr.restype = ctypes.wintypes.BOOL
    fnSetFileAttr.argtypes = ctypes.wintypes.LPWSTR, ctypes.wintypes.DWORD

    ATTR_FILE_HIDDEN = 0x02
    retVal = fnSetFileAttr(path, ATTR_FILE_HIDDEN)

    if not retVal:
        raise ctypes.WinError()
